mapper.insert:
insert into s_user
(username, password, nickname, type)
value (#{username}, #{password}, #{nickname},#{type});

mapper.update:
update s_user set
password=#{password}, nickname=#{nickname}, username=#{username}, type=#{type}
where id=#{id}

mapper.select:
select * from s_user 
where 1=1
 and ( username like #{name} or nickname like #{name} )
 and type=#{type} 

	order by ${sort}

			${order}
			asc


limit #{pageOffset}, #{pageSize}
